mapper.delete:
DELETE FROM users
WHERE id IN

    #{id}

mapper.select:
SELECT id, employee_id, username, password, real_name, email, phone, department_id, position, hire_date, status, avatar_url, created_at, updated_at FROM users
AND username LIKE CONCAT('%', #{username}, '%')
AND employee_id LIKE CONCAT('%', #{employeeId}, '%')
AND department_id = #{departmentId}
AND status = #{status}
AND position LIKE CONCAT('%', #{position}, '%')
AND created_at >= #{startTime}
ORDER BY id DESC

mapper.insert:
INSERT INTO users(
    employee_id, username, password, real_name, email,
    phone, department_id, position, hire_date,
    status, avatar_url
) VALUES (
             #{employeeId}, #{username}, #{password}, #{realName}, #{email},
             #{phone}, #{departmentId}, #{position}, #{hireDate},
             #{status}, #{avatarUrl}
         )
